#np.set_printoptions(threshold=np.inf)
def demod(signal):
	x = np.fromfile(signal,dtype=np.int8)
	#print(x)

	endarray=[]

	count = 0
	for i in range(2,len(x)):
		#print(x[i])
		if x[i]==x[i-1]:
			count+=1
		else:
			if x[i-1]==0:
				endarray.append(-count)
			else:
				endarray.append(count)
			count = 1

	#Ab hier ist es Codierungssache
	#print(endarray)
	#2return 0
	sta=""
	for i in range(0,len(endarray)):
		if (endarray[i]>600 and endarray[i] < 1000):
			endarray[i]="10"
			sta+="0"
			continue
		if(endarray[i] > 1000):
			endarray[i]="11"
			sta+="1"
			continue
		if endarray[i]< 100:
			endarray[i]="00"
			continue
		endarray[i]="01"

	#print(endarray)
	#string = ''.join(endarray)
	#print(hex(int(string,2)))
	#print(sta)
	print(hex(int(sta,2)))

